import {useState,useEffect} from "react"
import axios from "axios"
import { Link ,withRouter } from "react-router-dom"
import { connect } from "react-redux"



function Login(props)
{
  var [errorMessage,setErrorMessage]=useState()
  var [successMessage,setSuccessMessage]=useState()

  var [user,setUser]=useState({})
  var [usertoken,setUsertoken]=useState()

  //let [cart_data,setCart_data] = useState('');



    let getEmail=(event)=>
    {
        setUser({
            ...user,
            email:event.target.value,
        })
    }
    let getPassword=(event)=>
    {
        setUser({
            ...user,
            password:event.target.value,
        })
    }

    let login = ()=>{
        if(user.email && user.password){

        let apiurl="https://apibyashu.herokuapp.com/api/login"
           axios({
                url:apiurl,
                method:"post",
                data:user
           }).then((response)=>{
              if(response.data.token)
              {
                localStorage.token = response.data.token
                setErrorMessage("")
                setSuccessMessage("Logged In Successfully")
                setUsertoken(response.data.token)

                props.dispatch({
                  type:"LOGIN",
                  payload:response.data
                })


               // props.history.push("/")

              }else
              {
                setErrorMessage("Invalid credentials")
                setSuccessMessage("")
              }
           },(error)=>{
            setErrorMessage("Error from Login api "+error) 
            setSuccessMessage("") 
           })
        }
        else{
           setErrorMessage("Validation Failed")
           setSuccessMessage("")
        }
    }
    useEffect(()=>{
      let  token = localStorage.token 
      if(localStorage.token){
        console.log('token received  login useeffect')
        let getCartDetailsAPI = "https://apibyashu.herokuapp.com/api/cakecart"
        axios({
            url:getCartDetailsAPI,
            method:"post",
            data: {},
            headers : {
                authtoken: token
            }
        }).then((response)=>{
            console.log("appjs cakecart : ",response)
            props.dispatch({
              type:"CART_DATA",
              cart_data:response.data.data
          })
        }, (error)=>{
            console.log("appjs cakecart : ",error)
        })
      }
    },[usertoken])

		return(

			<div style={{width:"50%" , margin:"auto"}} className="col-md-6">
              <h2> Login Section </h2>
                <div className="form-group">
                    <label>Email</label>
                <input type="email" className="form-control" onChange={getEmail}></input>
                   { user?.email && <label>{user.email}</label> }
                </div>
                <div className="form-group">
                <label>Password</label>
                <input type="password" className="form-control" onChange={getPassword}></input>
                   {user?.password && <label>{user.password}</label> }
                </div>
                <div style={{color:"red"}}  className="text-center">
                    {errorMessage}
                </div>
                {successMessage?
                <div style={{color:"green"}} className="text-center">
                    {successMessage}
                </div>:''}
                <button className="btn btn-success form-control text-center my-2" onClick={login}>Login</button>

                <div style={{float:'right'}}>
                  <Link to="/forgot">Forgot password</Link>
                </div>
                <div>
                  <Link to="/signup">New User? Click Here</Link>
                </div>
            </div>
		)

}

Login = withRouter(Login)

export default connect(function(state,props){


  console.log('login state',state)
  console.log('login props',props)
  return{}
})(Login);
