//import { faPhone } from "@fortawesome/free-solid-svg-icons"
import axios from "axios"
import { useEffect } from "react"
import { useState } from "react"
import { connect } from "react-redux"
//import Address from "./Address"


function Order(props){

    var [apidata,setApidata] = useState({})
    var [cart_total_price, setCart_total_price]= useState()
    var TotalPrice = 0
    useEffect(()=>{
        console.log('order effect props ',props.user_cart)
        setApidata({
            ...apidata,
            cakes:props.user_cart?.user_cart,
            name:props.address?.name,
            address:props.address?.address,
            phone:props.address?.phone,
            city:props.address?.city,
            pincode:props.address?.pincode,
        })

        console.log('apidata',apidata)

        {props.user_cart?.length >0 && props.user_cart.map((each,index) => {
            TotalPrice += each.price
            })
            setApidata({
                ...apidata,
                price:TotalPrice
            })
        }

        // props.dispatch({
        //     type: "UPDATE_CART_TOTAL",
        //     payload: TotalPrice,
        // })
    },[])

    var confirmOrder = (e)=>{
        e.preventDefault();
        var token = localStorage.token
       // console.log('token',token)
        console.log('apidata',apidata)

        if(TotalPrice>0){
            axios({
                url: 'https://apibyashu.herokuapp.com/api/addcakeorder',
                method: "post",
                headers: {
                authtoken: token
                },
                data:apidata
            }).then((response) => {
                console.log('addcakeorder reponse',response)
                // props.dispatch({
                // type: "ORDER_DONE",
                // })

            }, (error) => {
                console.log("addcakeorder error: ", error)
            })
        }
    }

    return(
        <>
        <h4>Place Your Order</h4>
        <p>Click button below to  place your order. </p>
        <button className="btn btn-success" onClick={confirmOrder}>Next</button>
        </>
    )
}
export default connect((state,props)=>{
    //console.log('order state',state)
    return {
        user_cart:state?.user_cart,
        cart_total_price:state?.cart_total_price,
        address:state?.address
    }
})(Order)
