//import { faPhone } from "@fortawesome/free-solid-svg-icons"
import axios from "axios"
import { useState, useEffect } from "react"
import { connect } from "react-redux"
//import Address from "./Address"


function Order(props){

    var [apidata,setApidata] = useState({})
    var [cart_total_price, setCart_total_price]= useState()
    var TotalPrice = 0
    useEffect(()=>{
        props.user_cart.map((each,index) => {
            TotalPrice += each.price
            })
            setCart_total_price(TotalPrice)
    },[])

    var confirmOrder = (e)=>{
        e.preventDefault();
        var token = localStorage.token
       let data = props.address;
       data.price = cart_total_price;
       data.cakes = props.user_cart;
console.log('TotalPrice',TotalPrice)

        //if(TotalPrice>0){
            axios({
                url: 'https://apibyashu.herokuapp.com/api/addcakeorder',
                method: "post",
                headers: {
                authtoken: token
                },
                data:data
            }).then((response) => {
                console.log('addcakeorder reponse',response)
                props.dispatch({
                type: "ORDER_DONE",
                })

            }, (error) => {
                console.log("addcakeorder error: ", error)
            })
        //}
    }

    return(
        <>
        <h4>Place Your Order</h4>
        <p>Click button below to  place your order. </p>
        <button className="btn btn-success" onClick={confirmOrder}>Next</button>
        </>
    )
}
export default connect((state,props)=>{
    //console.log('order state',state)
    return {
        user_cart:state?.user_cart,
        cart_total_price:state?.cart_total_price,
        address:state?.address
    }
})(Order)
