import {connect} from "react-redux"
import {Link,withRouter } from "react-router-dom"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

import Cake from './Cake'
import axios from "axios"
import { faTrash } from '@fortawesome/free-solid-svg-icons'
import { useState, useEffect } from "react";

function CartSummary(props){
    var [cartDetails, setcartDetails]=useState([])
    var [stepone, setStepone]=useState(false)

    useEffect(()=>{
        let getCartDetailsAPI = "https://apibyashu.herokuapp.com/api/cakecart"
        let token = localStorage.token
        axios({
            url:getCartDetailsAPI,
            method:"post",
            data: {},
            headers : {
                authtoken: token
            }
        }).then((response)=>{
            console.log("response from cakes api : ",response)
            setcartDetails(response.data.data)
        }, (error)=>{
            console.log("response from cakes api : ",error)
        })
    }, [])
    var TotalPrice = 0
    var Count = 0
    // /addcaketocart

    let confirm_summary = function(e){
        //e.preventDefault();
        setStepone(true)

    }

    return(
        <>
        <table className="table table-hover">
        {cartDetails?.length >0  ? 
        <>
        {cartDetails?.length >0 && cartDetails.map((each,index) => {
            TotalPrice += each.price
            Count += 1

            return(

                <tr key={index}>
                    <td className="col-sm-3">
                        <img src={each.image} className="card-img-top" alt="..." style={{height:"40px", width:"40px"}}  />
                    </td>
                    <td className="col-sm-4">
                        {each.name}
                    </td>
                    <td className="col-sm-3">
                        {each.price}
                    </td>
                </tr>
            )
        })}   


            <tr>
                <th colSpan="2" style={{float:"right"}}>Total Amount</th>
                <th></th>
            </tr>
            </>
            :<tr><td colSpan="3">Cart is Empty</td></tr>
            }
        </table>
        </>
    )
}
export default CartSummary
