import { Route } from "react-router"
import Order from './Order' 
import Payment from './Payment' 
import Address from './Address' 
import CartSummary from "./CartSummary"
import {useRouteMatch, Link } from "react-router-dom"
import {connect} from "react-redux"
import {useEffect}  from "react"
import $ from 'jquery';




function Checkout(props){
  var LinkClass = "px-2 py-3 border-top border-bottom text-decoration-none "

    useEffect(() => {
        console.log($('ul.checkout-navbar > a:first'))
        $('ul.checkout-navbar > a:first').addClass('bg-warning text-white');
    }, []);
    console.log('checkout props',props)
    var route =  useRouteMatch()
    var url = route.url
    var path = route.path

    const navbarLink = (url,e, link_index) => {
      props.history.push(url)
      if(props.checkout_step >= link_index){
          $('ul.checkout-navbar > a > li')
                  .addClass('light-warning text-white');

          $('ul.checkout-navbar > a > li')
                  .removeClass('bg-warning');
          e.target.classList.add('bg-warning'); 
          e.target.classList.add('text-white');

      } else{
          $('ul.checkout-navbar > a > li')
                  .addClass('bg-light disabled-link text-muted');
      }

    };

   /*$(document).ready(function () {
        $('ul.navbar-nav > li')
                    .click(function (e) {
                $('ul.navbar-nav > li')
                    .removeClass('active');
                $(this).addClass('active');
            });

    });*/
  var currentpath = props.location.pathname;
 console.log('prop state',props.checkout_stage)
  console.log('currentpath',currentpath)
  console.log('url',url)
  console.log('prop state',props.checkout_stage)

//   useEffect(() => {
//     if (!props.token) {
//       props.history.push("/");
//     }
//   }, [props.token]);

    return(
      <>
        <h2 className="text-center mt-3">Checkout </h2>
        <div className="row mt-3">
             <div className="col-4 px-3">

            <ul class="nav navbar-nav text-center nav-bar-style checkout-navbar">
                <Link onClick={(e) => {navbarLink(url, e, 1);}}><li className={LinkClass }>Cart Summary</li></Link>
                <Link onClick={(e) => {navbarLink(url+"/address", e, 2);}}><li className={LinkClass}>Address</li></Link>
                <Link onClick={(e) => {navbarLink(url+"/payment", e, 3);}}><li className={LinkClass }>Payment</li></Link>
                <Link onClick={(e) => {navbarLink(url+"/order", e, 4);}}><li className={LinkClass }>Order</li></Link>
            </ul>




               {/* <ul className="nav navbar-nav text-left nav-bar-style checkout-navbar">
                <li><Link  className="disabled" to="/checkout">Cart Summary</Link></li>
                <li><Link to={url+"/address"}>Add Address</Link></li>
                <li><Link to={url+"/payment"}>Payment</Link></li>
                <li><Link to={url+"/order"}>Order</Link></li>
                <li><Link to={url+"/test"}>Test</Link></li>
                </ul>  */}
            </div>
            <div className="col-8 my-3">
                <Route exact path={path}  component={CartSummary} />
                <Route exact path={path+"/payment"}  component={Payment} />
                <Route exact path={path+"/address"}  component={Address} />
                <Route exact path={path+"/order"}  component={Order} />
                <Route exact path={path+"/test"}  component={Order} />

            </div>

        </div>
        </>
    )
}

export default connect(function (state, props) {
return {
    token: state?.user?.token,
    checkout_stage: state?.checkout_stage,
};
})(Checkout);
