import {connect} from "react-redux"
import {Link,withRouter } from "react-router-dom"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

import Cake from './Cake'
import axios from "axios"
import { faTrash } from '@fortawesome/free-solid-svg-icons'
import { useState, useEffect } from "react";

function CartSummary(props){
    var [cartDetails, setcartDetails]=useState([])
    var [stepone, setStepone]=useState(false)

    useEffect(()=>{
        let getCartDetailsAPI = "https://apibyashu.herokuapp.com/api/cakecart"
        let token = localStorage.token
        axios({
            url:getCartDetailsAPI,
            method:"post",
            data: {},
            headers : {
                authtoken: token
            }
        }).then((response)=>{
            setcartDetails(response.data.data)
        }, (error)=>{
        })
    }, [])
    var TotalPrice = 0
    var Count = 0

    let confirm_summary = (e)=>{
        console.log('dddd')
        props.dispatch({
            type: "CHECKOUT_STAGE",
            payload: 2,
        });
        props.history.push("/checkout/address");
    }

    return(
        <>
        <table className="table table-hover">
        {cartDetails?.length >0  ? 
        <>
        {cartDetails?.length >0 && cartDetails.map((each,index) => {
            TotalPrice += each.price
            Count += 1

            return(

                <tr key={index}>
                    <td className="text-center">
                        <img src={each.image} className=""  alt="..." style={{height:"40px", width:"40px"}}  />
                    </td>
                    <td className="text-center">
                        {each.name}
                    </td>
                    <td className="text-right">
                        {each.price}
                    </td>
                </tr>
            )
        })}   


            <tr>
            <td></td>
                <td  className="text-right"><b>Total Amount</b></td>
                <td className="text-right"><b>{TotalPrice}</b></td>
            </tr>
            <tr><td colSpan="3" className="text-center"> <button className="btn btn-success" onClick={confirm_summary}>Next</button> </td></tr>
            </>
            :<tr><td colSpan="3" className="text-danger">Cart is Empty</td></tr>
            }
        </table>
        </>
    )
}

export default connect(function (state, props) {
    return {
        token: state?.user?.token,
        checkout_stage: state?.checkout_stage,
    };
    })(CartSummary)
